    private void jtenviarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jtenviarActionPerformed
        Gerente g = new Gerente();
        g.setNome(jTnome.getText());
        g.setCpf(jTcpf.getText());
        g.setEndereco(jTendereco.getText());
        
        g.setTelefone(jTtel.getText());
        g.setSetor(jTsetor.getText());
        g.setAgencia(jTagencia.getText());
        
        g.setCtps(jTctps.getText());
        String salario = jTsalario.getText();
        double s = Double.parseDouble(salario);
        g.setSalario(s);
        g.setTelefone(jTtel.getText());
        BaseDadosGerentes.adicionar(g);
        BaseDadosGerentes.cadastra(g,"gerentes.dat");
        JOptionPane.showMessageDialog(this, "Gerente cadastrado!");
        getContentPane().remove(jtenviar);
        TelaCadastroGerente.this.dispose();
        jtenviar = null;
    }//GEN-LAST:event_jtenviarActionPerformed

